import React, { Component } from 'react'
import axios from 'axios'
import Item from './Article-Item'
import "./index.css"
export default class ArticleOnlineList extends Component {
  state = {
    essayList: [],
  }
  componentDidMount(){
    // console.log('componentDidMount');
    axios.get(`https://qcpi7s.fn.thelarkcloud.com/getArticleList`).then(
      response => {
        // console.log(response.data.result);
        this.setState({essayList:response.data.result})
      },
      error => {
        console.log("失败了",error.message);
      }
    )
  }
  render() {
    const {essayList}= this.state
    const onlineEssayList = essayList.filter((essayObj)=>{
      return essayObj.state===1;
    })
    return (
      <div className = "list">
        {
          onlineEssayList.map((essayObj)=>{
            return (<Item key={essayObj._id} {...essayObj}/>)
          })
        }
      </div>
    )
  }
}
