import React, { Component } from "react";
import "./index.css";
export default class Article extends Component {
  state = {
    title: "Title1",
    author: "新京报",
    date: "2021.1.1 12:31",
    essay:
      "新华社贵阳2月5日电 中华民族传统节日农历牛年春节即将到来之际，中共中央总书记、国家主席、中央军委主席习近平来到贵州，看望慰问各族干部群众，向全国各族人民、向港澳台同胞和海外侨胞致以美好的新春祝福，祝福大家身体健康、家庭幸福、事业顺利、牛年吉祥！祝愿伟大祖国山川锦绣、欣欣向荣、繁荣富强！",
    favor: 11,
    isFavor: false,
    dislike: 7,
    isDislike: false,
  };
  //更新点赞数
  updateFavor = () => {
    const { favor, isFavor,dislike,isDislike } = this.state;
    this.setState({
      favor: !isFavor ? favor + 1 : favor - 1,
      isFavor: !isFavor,
      dislike:!isFavor&&isDislike?dislike-1:dislike,
      isDislike:!isFavor&&isDislike?!isDislike:isDislike
    });
    console.log(this);
  };
  updateDislike=()=>{
    const { dislike, isDislike,favor, isFavor } = this.state;
    this.setState({
      dislike: !isDislike ? dislike + 1 : dislike - 1,
      isDislike: !isDislike,
      favor:!isDislike&&isFavor?favor-1:favor,
      isFavor:!isDislike&&isFavor?!isFavor:isFavor
    });
    console.log(this);
  }
  render() {
    const { title, author, date, essay, favor, dislike, isFavor,isDislike } = this.state;
    return (
      <div className="article-content">
        <h1>{title}</h1>
        <div className="article-meta">
          {author}&nbsp;&nbsp;{date}
        </div>
        <div className="essay-box">{essay}</div>
        <div className="button-box">
          <button onClick={this.updateFavor} style={{backgroundColor:isFavor?'lightblue':'#ccc'}}>赞 {favor}</button>
          <button onClick={this.updateDislike} style={{backgroundColor:isDislike?'lightblue':'#ccc'}}>踩 {dislike}</button>
        </div>
      </div>
    );
  }
}
