import React, { Component } from 'react'
import axios from 'axios'
import "./index.css"
export default class Edit extends Component {
  componentDidMount(){
    axios.get(`https://qcpi7s.fn.thelarkcloud.com/getArticleById`,{
      params:{
        articleID:this.props.match.params.articleID
      }
    }).then(
      response => {
        console.log(response.data.result);
        const {title,source,updatedAt,content} = response.data.result
        this.setState({title,source,updatedAt,content})
      },
      error => {
        console.log("失败了",error.message);
      }
    )
  }
  render() {
    return (
      <div>
        <h2>文章编辑</h2>
        <div>
          标题 <input type="text"/>
        </div>
      </div>
    )
  }
}
