import React, { Component } from 'react'
import axios from 'axios'
import "./index.css"
export default class Edit extends Component {
  state={
    essay:[]
  }
  componentDidMount(){
    axios.get(`https://qcpi7s.fn.thelarkcloud.com/getArticleById`,{
      params:{
        articleID:this.props.match.params.articleID
      }
    }).then(
      response => {
        console.log(response.data.result);
        this.setState({essay:response.data.result})
      },
      error => {
        console.log("失败了",error.message);
      }
    )
  }
  render() {
    const { title,source,content } = this.state
    return (
      <div>
        <h2>文章编辑</h2>
        <div>
          标题 <input type="text" value={title}/>
          来源 <input type="text" value={source}/>
          内容 <input type="text" value={content}/>
        </div>
      </div>
    )
  }
}
