import React, { Component } from 'react'
import {Link} from 'react-router-dom'
import axios  from 'axios'
import Header from '../../components/Header'
import "./index.css"
export default class EditArticle extends Component {
  state={
    essayList:[]
  }
  componentDidMount(){
    axios.get(`https://qcpi7s.fn.thelarkcloud.com/getArticleList`).then(
      response => {
        // console.log(response.data.result);
        this.setState({essayList:response.data.result})
      },
      error => {
        console.log("失败了",error.message);
      }
    )
  }
  render() {
    const { essayList } = this.state
    return (
        <div>
          <Header active2="active"/>
          <div className="editList">
            <h2>已有文章列表</h2>
            <table className="table-box">
              <thead>
                <tr>
                  <td>序号</td>
                  <td>标题</td>
                  <td>来源</td>
                  <td>日期</td>
                  <td>状态</td>
                  <td>操作</td>
                </tr>
              </thead>
              <tbody>
                {
                  essayList.map((essayObj)=>{
                    return(
                      <tr key={essayObj._id}>
                        <td>{essayObj.articleId}</td>
                        <td>{essayObj.title}</td>
                        <td>{essayObj.source}</td>
                        <td>{essayObj.updatedAt}</td>
                        <td>{essayObj.state?'线上':'线下'}</td>
                        <td>
                          <Link className="edit-article" to={`/editExisted/${essayObj.articleId}`} target="_blank">编辑</Link>&nbsp;
                          <button className="state_button" onClick={this.changeState}>{essayObj.state?'下线':'上线'}</button> 
                        </td>
                      </tr>
                    )
                  })
                }
              </tbody>
            </table>
            <div className="new-article">
              <Link className="new-article-link" to={"/editEmpty"} target="_blank">文章录入</Link>
            </div>
          </div>
        </div>
      )
  }
}
