import React, { Component } from 'react'
import axios from 'axios'
import "./index.css"
export default class Edit extends Component {
  state={
    title:'',
    articleID:'',
    source:'',
    content:'',
    state1:0
  }
  getTitle=(event)=>{
    console.log(event.target.value);
    this.setState({title:event.target.value})
  }
  getSource=(event)=>{
    this.setState({source:event.target.value})
  }
  getContent=(event)=>{
    this.setState({content:event.target.value})
  }
  submitEssay=()=>{
    const { title,articleId,source,content,state1 } = this.state
    console.log("提交了",this.state);
    axios.post(`https://qcpi7s.fn.thelarkcloud.com/saveArticle`,{
      params:{
        title:title,
        articleId:articleId,
        source:source,
        content:content,
        state:state1
      }
    }).then(
      response => {console.log("成功了",response.data.result);},
      error => {console.log("失败了",error.message);}
    )
  }
  componentDidMount(){
    var that = this
    axios.get(`https://qcpi7s.fn.thelarkcloud.com/getArticleById`,{
      params:{
        articleId:this.props.match.params.articleId
      }
    }).then(
      response => {
        console.log(response.data.result);
        const {title,articleId,source,content,state} = response.data.result;
        that.setState({title,articleId,source,content,state1:state})
      },
      error => {
        console.log("失败了",error.message);
      }
    )
  }
  render() {
    const { title,source,content } = this.state
    console.log("state:",this.state);
    return (
      <div>
        <h2>文章编辑</h2>
        <div>
          标题 <input onChange={this.getTitle} type="text" defaultValue={title}/>
        </div>
        <div>
          来源 <input onChange={this.getSource} type="text" defaultValue={source}/>
        </div>
        <div>
          内容 <input onChange={this.getContent} type="text" defaultValue={content}/>
        </div>
        <button onClick={this.submitEssay}>提交</button>
      </div>
    )
  }
}
